mapper.update:
UPDATE lightning_meeting
SET organizer_id = #{newOrganizerId}
WHERE id = #{meetingId}

UPDATE lightning_meeting
SET status = #{status}
WHERE id = #{meetingId}

mapper.select:
SELECT * FROM lightning_meeting
WHERE recruitment_end_time <= #{now}
AND status = #{status};

SELECT *
FROM lightning_meeting
WHERE recruitment_end_time > #{now}
AND status = #{status}
AND min_person_num <= (
SELECT COUNT(*)
FROM lightning_meeting_participant
WHERE meeting_id = lightning_meeting.id
)

mapper.update:
UPDATE lightning_meeting
SET status = 'CANCELLED'
WHERE id = #{meetingId}

UPDATE lightning_meeting
SET organizer_id = #{userId}
WHERE id = #{meetingId}

UPDATE lightning_meeting
SET notification_sent = TRUE
WHERE id = #{meetingId}

mapper.select:
SELECT * FROM lightning_meeting
WHERE start_time <= #{now}
AND status != 'SUCCESS'
